var config = require('config')
var User =   require('db/user')
var pages =  require('pages')
var sec = require('securityutil')

// todo: move this page to the root
module.exports = function(params, body, cookies) {
    var data = User.crackToken(cookies.token)
    if(data) {
        var body = pages.pageWrapper(main(data.id))
    } else {
        var body = pages.pageWrapper(pages.login())
    }
	return {body: body, headers:{"content-type": "text/html"}}
}


function main(id) {
    return  '<div id=main>'
                +pages.logout()
                +pages.profile(id)
                +pages.transactions()
           +"</div>"
}

